mapper.select:
select total_time as totalTime, SUM(total_number) as totalNumber, SUM(total_weight) as totalWeight, SUM(total_offer) as totalOffer, SUM(total_paid) as totalPaid, SUM(total_additional) as totalAdditional, SUM(total_cost) as totalCost from total
and FIND_IN_SET(total_state,#{totalState})
and total_time=#{totalTime}
and FIND_IN_SET(user_id,#{userId})
and send_id=#{sendId}

select total_id as totalId, sum_id as sumId from total
and FIND_IN_SET(sum_id,#{sumId})
and total_time=#{totalTime}
and total_state >= #{state}

select total_id as totalId, send_id as sendId, t.user_id as userId, order_no as orderNo, t.`name`, total_time as totalTime, total_number as totalNumber, total_weight as totalWeight, total_offer as totalOffer,
total_cost as totalCost,
total_additional as totalAdditional, total_paid as totalPaid, total_credentials_url as totalCredentialsUrl, total_url as totalUrl, total_remark as totalRemark, total_state as totalState, t.create_time as createTime, as_of_time as asOfTime, t.update_time as updateTime, type_name as typeName, payee, payment_account as paymentAccount from total t LEFT JOIN sys_user_info ui on ui.id=t.user_id LEFT join payment_method m on m.user_id=ui.parent_id
and t.user_id =#{userId}
and total_time =#{totalTime}
and total_state>1
and total_state=#{state}
and send_id = #{sendId}

select
SUM(total_paid) as totalPaid
from total where user_id in (${userId}) and total_time =#{totalTime} and total_state in(${state})

select t.name as name, t.total_offer as totalOffer, t.total_paid as totalPaid, t.total_state as totalState, t.total_credentials_url as totalCredentialsUrl, u.name as nameNick, u.person_in_charge as personInCharge, u.telephone from total t LEFT JOIN sys_user_info u on u.id=t.user_id
t.send_id=#{id} and FIND_IN_SET(t.user_id,#{userId})
and t.total_time BETWEEN #{date} and #{endDate}
and t.total_time=#{date}
and t.total_state=#{status}
and t.total_state>1
